  `deposit` int(2) NOT NULL DEFAULT '1' COMMENT '是否已担保金@0 1:未领取,2已领取补贴',
  `bePunished` int(2) NOT NULL DEFAULT '1' COMMENT '被别人处罚 1未处理:2 处理',
  `punished` int(2) NOT NULL DEFAULT '1' COMMENT '处罚别人     1未处理:2 处理',
